params.command:
$services = Get-WmiObject Win32_Service | Select-Object Name, Pathname | Where-Object Pathname -NotLike 'C:\WINDOWS\system32\svchost.exe*' | Sort-Object Pathname -Unique
$formatted_serv_list = @()
foreach($service in $services){
    $serv_path = $service.Pathname -replace "`"", ""
    $serv_path = $serv_path -match ".*\.exe(?!\S)"
    $serv_path = $Matches[0]
    if($serv_path){
        $non_os_service = Get-AuthenticodeSignature -FilePath $serv_path | Select-Object Path, IsOSBinary, Status | Where-Object IsOSBinary -NE "True"
        }
    else{}
    if($non_os_service){
    $hashed_serv = Get-FileHash -Algorithm MD5 -Path $non_os_service.Path
    $formatted_serv = New-Object System.Object
    $formatted_serv | Add-Member -MemberType NoteProperty -Name “Name” -Value $service.Name
    $formatted_serv | Add-Member -MemberType NoteProperty -Name “Path” -Value $serv_path
    $formatted_serv | Add-Member -MemberType NoteProperty -Name “MD5” -Value $hashed_serv.Hash
    $formatted_serv | Add-Member -MemberType NoteProperty -Name “Status” -Value $non_os_service.Status
    $formatted_serv_list += $formatted_serv
    }
    else{}
}

$formatted_serv_list = $formatted_serv_list | Sort-Object Path -Unique | sort-object Name | ConvertTo-Json
$formatted_serv_list
